import { useState } from "react"
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
function Signup() {
    const [formData, setFormData] = useState({
        fullname:'',
        email: '', 
        password: '', 
        confirmpassword: '' 
    })
     const [fullname,setFullname]=
     function validate(e){
        
        e.preventDefault()
        if(fullname<6){
            alert("Enter name correctly")
        }
        else if(password!=confirmpassword){
            alert("Password does not match")
        }
        else{
            alert("Successfully registered..!!")
        }
     }
    function handleSubmit(e) {
        e.preventDefault()
        fetch('http://localhost:3006/users', {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify(formData)
        })
        .then(res => res.json())
        .then(data => console.log(data))
    }

    function handleChange(e) {
        setFormData({...formData, [e.target.name] : e.target.value})
    }

    return (
        <div>
            <h1>Signup</h1><br/><br/>
            <form onSubmit={e => handleSubmit(e)  && validate(e)}>
            <div class="row mb-3">
            <label  class="col-sm-2 col-form-label">Full Name</label>
            <div class="col-sm-10">
             <input type="text" name="fullname"value={formData.username}onChange={e => handleChange(e)} class="form-control" />
             </div>
             </div>
            <div class="row mb-3">
            <label  class="col-sm-2 col-form-label">Email</label>
            <div class="col-sm-10">
             <input type="email" name="email" value={formData.email} onChange={e => handleChange(e)}class="form-control" id="inputEmail3"/>
             </div>
             </div>
             <div class="row mb-3">
             <label class="col-sm-2 col-form-label">Password</label>
              <div class="col-sm-10">
              <input type="password" name="password" value={formData.password} onChange={e => handleChange(e)}class="form-control" id="inputPassword3"/>
              </div>
              </div>
              <div class="row mb-3">
             <label class="col-sm-2 col-form-label">Confirm Password</label>
              <div class="col-sm-10">
              <input type="password" name="confirmpassword"value={formData.confirmpassword}class="form-control" onChange={e => handleChange(e)}id="inputPassword3"/>
              </div>
              </div><br/><br/>
              <Link type='submit' style={{marginRight:"2%",marginLeft:"2%"}} class="btn btn-primary" to="/Login">SignUp</Link>
              <Link type='submit'  style={{marginRight:"2%",marginLeft:"2%"}}class="btn btn-secondary" to="/">Back to main page</Link>
              </form>
           </div>   
            )
        }
        
        export default Signup
