import {useState} from "react";
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
import EmpTable from "./EmpTable"
import { useNavigate } from "react-router-dom";
function Login(){
    const [email1,setEmail]=useState("")
    const [pswd1,setPswd]=useState("")
    const navigate = useNavigate()
    const update1=(e)=>{
        setEmail(e.target.value)
   }
   const update2=(e)=>{
    setPswd(e.target.value)
  }
 
  const handleSubmit=(e)=>{
    e.preventDefault()
    const data={id,name,email,mobile,salary}
       fetch("http://localhost:3006/Employee",{
        method:"POST",
        headers:{"content-type":"application/json"},
        body:JSON.stringify(data)
       })
       .then(()=>{
        alert("saved succesfully..!!")
        navigate("/")
       })
       .catch(()=>{
        alert("Error....")
       })
  }
    return(
        <div>
            <h2>Login</h2><br/>
            <form>
            <div className="form-floating mb-3">
         <input type="email" onChange={update1} value={email1} required  className="form-control" /><br/>
         <label>Email address</label>
         </div>
         <div className="form-floating">
         <input type="password" onChange={update2}value={pswd1} required className="form-control" /><br/>
         <label>Password</label>
          <Link className="btn btn-success" style={{marginRight:"2%",marginLeft:"2%"}} to="/EmpTable" >Submit</Link>
          
         
         </div>
            </form>
        </div>
    )
}
export default Login
