import { useState } from "react";
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"


const Login=()=>{
const [name,nameupdate]=useState("");
const [pswd,pswdupdate]=useState("");


const validate=(e)=>{
    e.preventDefault();
    if(name.length<5 && name==" "){
        alert("Enter name correctly..") 
     }
     else if(pswd==" "){
         alert("Enter Password..")
     }
     else{
          fetch("http://localhost:3006/users/"+fullname).then((res)=>{
            return res.json()
          })
     }
     
}
    return(
      <div>
          <div className="offset-lg-3 col-lg-6">
                <form className="container" onSubmit={validate}>
                    <div className="card">
                        <div className="card-header">
                           <h1>Login</h1> 
                        </div>
                        <div className="card-body">
                            <div className="column">
                                <div className="col-lg-6">
                                   <div className="form-group">
                                    <label>Full Name</label>
                                    <input  value={name} onChange={e=>nameupdate(e.target.value)} type="text"className="form-control"></input>
                                   </div>
                                </div>
                                <div className="col-lg-6">
                                   <div className="form-group">
                                    <label>Password</label>
                                    <input value={pswd} onChange={e=>pswdupdate(e.target.value)}type="password"className="form-control"></input>
                                   </div>
                                </div>
                               
                            </div>
                        </div>
                        <div className="card-footer">
                        <Link type='submit'  style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-primary" to="/EmpTable">Login</Link> 
                        <Link type='submit'  style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-secondary" to="/SignUp">New Employee</Link> 
                        </div>
                    </div>
                </form>
          </div>
      </div>
    );
}
export default Login;
