import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./App.css"
function EmpTable() {
    const [data, setData] = useState(null)
    const [value,setValue]=useState("")
    const [sort,setSort]=useState("")
    const navigate = useNavigate()
    
const options=["name","email","mobile","salary"]

const itemsPerPage = 10; // Number of items to display per page

const Pagination = ({ data }) => {
  const [currentPage, setCurrentPage] = useState(1);

  // Calculate total number of pages based on number of items and items per page
  const totalPages = Math.ceil(data.length / itemsPerPage);

  // Extract subset of items to display for current page
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentItems = data.slice(startIndex, endIndex);

  // Handle page navigation events
  const handlePageClick = (pageNum) => {
    setCurrentPage(pageNum);
  };
  const renderPageLinks = () => {
    const pageLinks = [];

    for (let i = 1; i <= totalPages; i++) {
      const isActive = i === currentPage;
      pageLinks.push(
        <button key={i} onClick={() => handlePageClick(i)} disabled={isActive}>
          {i}
        </button>
      );
    }

    return pageLinks;
  };

   


    const onDelete = (id) => {
        if (window.confirm("Do you want to delete?")) {
            fetch("http://localhost:3006/Employee/" + id, {
                method: "DELETE"
            })
                .then(() => {
                    alert("Deleted successfully..!!")
                    window.location.reload()
                })
        }
    }

    const onEdit = (id) => {
        navigate("/empedit/" + id)
    }
    const onDetails = (id) => {
        navigate("/empdetails/" + id)
    }

    const SearchData=(e)=>{
        console.log(e.target.value)
        setValue(e.target.value)
    }
    const handleSubmit=async (e)=>{
  e.preventDefault()
        return await axios.get(`http://localhost:3006/Employee?q=${value}`)
        .then((res)=>{
            setData(res.data)
            setValue("")
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    const reset=(e)=>{
        e.preventDefault()
        fetch("http://localhost:3006/Employee")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                
                setData(resp)
            })
    }

    const sortData=async (e)=>{
        e.preventDefault()
        let value=e.target.value
        setSort(value)
        return await axios.get(`http://localhost:3006/Employee?_sort=${value}&_order=asc`)
        .then((res)=>{
            setData(res.data)
        })
        .catch((err)=>{
            console.log(err)
        })

    }

   


    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Search</label>
                    <input type="text" value={value}  onChange={SearchData} className="form-control" />
                </div>
                <button type="submit" style={{marginRight:"2%"}} className="btn btn-primary">Search</button>
                <button onClick={reset} style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-primary">Reset</button>
            </form><br/>
            <select value={sort} onChange={sortData}>
                <option>Choose option</option>
                {options.map((item)=>(
                    <option>{item}</option>
                ))}
            </select><br/><br/>
            <div className="card">
                <div className="card-title">
                    <h3 style={{textAlign:"center"}}>Besant Employee Management System</h3>
                    
                </div>
                <div className="card-body">

                    <Link to="/form" className="btn btn-success">
                        Add New (+)
                    </Link><br/><br/>
                    <table className="table table-bordred">
                        <thead style={{backgroundColor:"black",color:"white"}}>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Mobile</th>
                                <th>Salary</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody style={{backgroundColor:"lightskyblue"}}>
                            {data && data.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.salary}</td>
                                    <td>
                                        <a onClick={() => { onDelete(item.id) }} style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-danger">Delete</a>
                                        <a onClick={() => { onEdit(item.id) }} style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-success">Edit</a>
                                        <a onClick={() => { onDetails(item.id) }} style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-primary">Details</a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div>
      <ul>
        {currentItems.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
      <div>
        {renderPageLinks()}
      </div>
    </div>
            </div>

        </div>
    )
        }
    }
    export default EmpTable
