import EmpTable from "./EmpTable"
import { useNavigate } from "react-router-dom";
function Login(){
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const navigate = useNavigate()
    const update1=(e)=>{
        setEmail(e.target.value)
   }
   const update2=(e)=>{
    setPswd(e.target.value)
  }
 
  
    return(
        <div>
            <h2>Login</h2><br/>
            <form onSubmit={handleSubmit}>
            <div className="form-floating mb-3">
         <input type="email" onChange={update1} value={email} required  className="form-control" /><br/>
         <label>Email address</label>
         </div>
         <div className="form-floating">
         <input type="password" onChange={update2}value={pswd} required className="form-control" /><br/>
         <label>Password</label>
          <Link className="btn btn-success" style={{marginRight:"2%",marginLeft:"2%"}} to="/EmpTable" >Submit</Link>
          
         
         </div>
            </form>
        </div>
    )
}
export default Login
