import "./App.css"

function App(){
  return(
    <div className="reg" style={{ 
      backgroundImage: `url("https://images.unsplash.com/photo-1572021335469-31706a17aaef?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTV8fGVtcGxveWVlfGVufDB8fDB8fA%3D%3D&auto=format&fit=crop&w=600&q=60")` ,
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      height:'100vh',
      marginTop:'0px',
    }}>
       <h1>Besant Employee Management System</h1><br/><br/>
       <button type="button" style={{marginTop:"20%",marginRight:"2%",marginLeft:"2%"}} class="btn btn-info">Register</button>
       <button type="button" style={{marginTop:"20%",marginRight:"2%",marginLeft:"2%"}} class="btn btn-info">Login</button>
    </div>
  )
}
export default App
