import {useState} from "react";
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
function Reg(){
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const [cpswd,setCpswd]=useState("")
    
    const update=(e)=>{
      console.log(e.target.value)
         setName(e.target.value)
    }
    const update1=(e)=>{
      setEmail(e.target.value)
 }
 const update2=(e)=>{
  setPswd(e.target.value)
}
const update3=(e)=>{
  setCpswd(e.target.value)
}

const handleSubmit=(e)=>{
  e.preventDefault()
  const data={name,email,pswd,cpswd}
     fetch("",{
      method:"POST",
      headers:{"content-type":"application/json"},
      body:JSON.stringify(data)
     })
     .then(()=>{
      alert("saved succesfully..!!")
     })
     .catch(()=>{
      alert("Error....")
     })
}
    return(
      <div>
        <h2>Register</h2><br/>
         <form onSubmit={handleSubmit}>
         <div className="form-floating mb-3">
         <input type="text" onChange={update} value={name} required className="form-control" /><br/>
         <label>Fullname</label>
         </div>
         <div className="form-floating mb-3">
         <input type="email" onChange={update1} value={email} required  className="form-control" /><br/>
         <label>Email address</label>
         </div>
         <div className="form-floating">
         <input type="password" onChange={update2}value={pswd} required className="form-control" /><br/>
         <label>Password</label>
         </div>
         <div className="form-floating">
         <input type="password" onChange={update3} value={cpswd} required className="form-control" /><br/>
         <label>Confirm Password</label>
         <button type="submit" style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-success">Submit</button>
      
         <Link className="btn btn-secondary" style={{marginRight:"2%",marginLeft:"2%"}} to="/Login">Back</Link>
         </div>
         </form>
      </div>
    )
  }
  export default Reg
