import { useState } from "react"
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
function Signup() {
    const [formData, setFormData] = useState({
        fullname:'',
        email: '', 
        password: '', 
        confirmpassword: '' 
    })

    function handleSubmit(e) {
        e.preventDefault()
        fetch('http://localhost:3006/users', {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify(formData)
        })
        .then(res => res.json())
        .then(data => console.log(data))
    }

    function handleChange(e) {
        setFormData({...formData, [e.target.name] : e.target.value})
    }

    return (
        <div>
            <h1>Signup Form</h1>
            <form className='login-form' onSubmit={e => handleSubmit(e)}>
                <input type='text' placeholder='Fullname' value={formData.username} name='fullname' onChange={e => handleChange(e)} ></input>
                <input type='text' placeholder='Email' value={formData.email} name='email' onChange={e => handleChange(e)} ></input>
                <input type='text' placeholder='Password' value={formData.password} name='password' onChange={e => handleChange(e)} ></input>
                
                <Link className='login-btn' type='submit' to="/Login">SignUp</Link>
            </form>
        </div>
    )
}

export default Signup
