import {useState} from "react";
import { useNavigate } from "react-router-dom";
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
function EmpEdit(){
    const[id,setId]=useState("")
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [mobile,setMobile]=useState("")
    const [salary,setSalary]=useState("")
    const navigate=useNavigate()
    const {empid}=useParams()
    const update=(e)=>{
         setName(e.target.value)
    }
    const update1=(e)=>{
      setEmail(e.target.value)
 }
 const update2=(e)=>{
  setMobile(e.target.value)
}
const update3=(e)=>{
  setSalary(e.target.value)
}
const update4=(e)=>{
    setId(e.target.value)
}
useEffect(() => {
    fetch("http://localhost:3006/Employee")
    .then((res) => {
        return res.json()
    })
    .then((resp) => {
        console.log(resp)
        setData(resp)
    })
}, [])
const handleSubmit=(e)=>{
  e.preventDefault()
  const data={id,name,email,mobile,salary}
     fetch("http://localhost:3006/Employee",{
      method:"POST",
      headers:{"content-type":"application/json"},
      body:JSON.stringify(data)
     })
     .then(()=>{
      alert("saved succesfully..!!")
      navigate("/")
     })
     .catch(()=>{
      alert("Error....")
     })
}

    return(
      <div>
        <h2>Register</h2><br/>
         <form onSubmit={handleSubmit}>
         <div className="form-floating mb-3">
         <input type="text" onChange={update4} value={id} disabled="disabled" required className="form-control" /><br/>
         <label>Id</label>
         </div>
         <div className="form-floating mb-3">
         <input type="text" onChange={update} value={name} required className="form-control" /><br/>
         <label>Fullname</label>
         </div>
         <div className="form-floating mb-3">
         <input type="email" onChange={update1} value={email} required  className="form-control" /><br/>
         <label>Email address</label>
         </div>
         <div className="form-floating">
         <input type="number" onChange={update2}value={mobile} required className="form-control" /><br/>
         <label>Mobilenumber</label>
         </div>
         <div className="form-floating">
         <input type="number" onChange={update3} value={salary} required className="form-control" /><br/>
         <label>Salary</label>
         <button type="submit" style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-primary">Update</button>
         <Link className="btn btn-secondary" to="/">Back</Link>
         </div>
         </form>
      </div>
    )
  }
  export default EmpEdit;
