import React, { useState } from 'react';


const Signup= () => {
    const [name,setName]=useState()
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !email || !password || !confirmPassword) {
      setError('Please fill all the fields.');
    } else if (password !== confirmPassword) {
      setError('Passwords do not match.');
    } else {
      // Send registration data to server for authentication
      // On successful registration, redirect user to login page
      // On authentication failure, show appropriate error message
      // You can use a library like axios to make HTTP requests to the server
    }
  };

  return (
    <form onSubmit={handleSubmit}>
        <div>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div>
        <label>Confirm Password:</label>
        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
      </div>
      {error && <div>{error}</div>}
      <button type="submit">Register</button>
    </form>
  );
};

export default Signup;
