import React, { useState } from 'react';


const Signup= () => {
    const [name,setName]=useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    fullname:'',
    email: '', 
    password: '', 
    confirmpassword: '' 
})
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !email || !password || !confirmPassword) {
      setError('Please fill all the fields.');
    } else if (password !== confirmPassword) {
      setError('Passwords do not match.');
    } else {
      
      fetch('http://localhost:3006/users', {
        method: 'POST',
        headers: {'Content-Type' : 'application/json'},
        body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => console.log(data))
    }
  };
  function handleChange(e) {
    setFormData({...formData, [e.target.name] : e.target.value})
}

  return (
    <form onSubmit={handleSubmit}>
        <div>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value) && handleChange(e)} />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value) && handleChange(e)} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value) && handleChange(e)} />
      </div>
      <div>
        <label>Confirm Password:</label>
        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value) && handleChange(e)} />
      </div>
      {error && <div>{error}</div>}
      <Link type='submit'style={{marginRight:"2%",marginLeft:"2%"}} className="btn btn-primary" to="/L">Login</Link>
    </form>
  );
};

export default Signup;
