import { useState } from "react"
import { BrowserRouter as Router,Routes,Route,Link } from "react-router-dom"
function Signup() {
    const [formData, setFormData] = useState({
        fullname:'',
        email: '', 
        password: '', 
        confirmpassword: '' 
    })
    const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmpassword] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(false);
 
  const handleName = (e) => {
    setName(e.target.value);
    setSubmitted(false);
  };
 

  const handleEmail = (e) => {
    setEmail(e.target.value);
    setSubmitted(false);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
    setSubmitted(false);
  };
 
  // Handling the form submission
  const submit = (e) => {
    e.preventDefault();
    if (name === '' || email === '' || password === '') {
      setError(true);
    } else {
      setSubmitted(true);
      setError(false);
    }
  };
  const successMessage = () => {
    return (
      <div
        className="success"
        style={{
          display: submitted ? '' : 'none',
        }}>
        <h1>User {name} successfully registered!!</h1>
      </div>
    );
  };
 
  // Showing error message if error is true
  const errorMessage = () => {
    return (
      <div
        className="error"
        style={{
          display: error ? '' : 'none',
        }}>
        <h1>Please enter all the fields</h1>
      </div>
    );
  };

    function handleSubmit(e) {
        e.preventDefault()
        fetch('http://localhost:3006/users', {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify(formData)
        })
        .then(res => res.json())
        .then(data => console.log(data))
    }

    function handleChange(e) {
        setFormData({...formData, [e.target.name] : e.target.value})
    }

    return (
        <div>
            <h1>Signup</h1><br/><br/>
            <div className="messages">
        {errorMessage()}
        {successMessage()}
      </div>
            <form onSubmit={e => handleSubmit(e)}>
            <div class="row mb-3">
            <label  class="col-sm-2 col-form-label">Full Name</label>
            <div class="col-sm-10">
             <input type="text" name="name"value={formData.username}onChange={e => handleChange(e) && {handleName}} class="form-control" />
             </div>
             </div>
            <div class="row mb-3">
            <label  class="col-sm-2 col-form-label">Email</label>
            <div class="col-sm-10">
             <input type="email" name="email" value={formData.email} onChange={e => handleChange(e) && {handleEmail}}class="form-control" id="inputEmail3"/>
             </div>
             </div>
             <div class="row mb-3">
             <label class="col-sm-2 col-form-label">Password</label>
              <div class="col-sm-10">
              <input type="password" name="password" value={formData.password} onChange={e => handleChange(e) && {handlePassword}}class="form-control" id="inputPassword3"/>
              </div>
              </div>
              <div class="row mb-3">
             <label class="col-sm-2 col-form-label">Confirm Password</label>
              <div class="col-sm-10">
              <input type="password" name="confirmpassword"value={formData.confirmpassword}class="form-control" onChange={e => handleChange(e)}id="inputPassword3"/>
              </div>
              </div><br/><br/>
              <Link type='submit' onClick={submit}style={{marginRight:"2%",marginLeft:"2%"}} class="btn btn-primary" to="/Login">SignUp</Link>
              <Link type='submit'  style={{marginRight:"2%",marginLeft:"2%"}}class="btn btn-secondary" to="/">Back to main page</Link>
              </form>
           </div>   
            )
        }
        
        export default Signup
