import {useState} from "react"

function Reg(){
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const [cpswd,setCpswd]=useState("")
    const update=(e)=>{
         setName(e.target.value)
    }
    const update1=(e)=>{
      setEmail(e.target.value)
 }
 const update2=(e)=>{
  setPswd(e.target.value)
}
const update3=(e)=>{
  setCpswd(e.target.value)
}
const handleSubmit=()=>{
  if(name.length<=5){
    alert("Name should be greater than 5 characters..")
  }
  else if(pswd!=cpswd){
    alert("Password does not match..")
  }
  else{
    alert("Successfully Registered....!")
  }
}
    return(
      <div>
         <form onSubmit={handleSubmit}>
         <div class="form-floating mb-3">
         <input type="text" onChange={update}value={name} required class="form-control" /><br/>
         <label>Fullname</label>
         </div>
         <div class="form-floating mb-3">
         <input type="email" onChange={update1}value={email} required  class="form-control" /><br/>
         <label>Email address</label>
         </div>
         <div class="form-floating">
         <input type="password" onChange={update2}value={pswd} required class="form-control" /><br/>
         <label>Password</label>
         </div>
         <div class="form-floating">
         <input type="password" onChange={update3}value={cpswd} required class="form-control" /><br/>
         <label>Confirm Password</label>
         </div>
         </form>
      </div>
    )
  }
  export default Reg
